mapper.select:
select * from
(select rownum rn, tt.* from
(select * from noticeboard order by notice_seq desc)tt
)where rn >= #{startNum} and rn <= #{endNum}


		select * from 
		(select rownum rn, tt. * from 
 		(select * from noticeboard where ${itemcd} like '%'||#{keyword}||'%' 
 		                     order by notice_seq desc)tt
 		)where rn >= #{startNum} and rn <= #{endNum}
